// get betting error Command failed: /Volumes/Data/workspaces/komodo/atomicdex/packages/dice-app/app/bin/mac/komodo-cli -ac_name=KMDICE sendrawtransaction  code: -26error message:18: bad-txns-inputs-spent

export default function sendrawtransactionFactory(komodod) {
  const action = 'sendrawtransaction';
  const { coin } = config.get('chains');
  return async ({ args }: { args: Array<*> }) => {
    try {
      if (komodod.isRunning() !== true) {
        throw new Error('komodod has not started yet');
      }
      const rs = await komodod.rpc({
        coin,
        action,
        args
      });
      return {
        ok: 'done',
        bettxid: rs.trim()
      };
    } catch (err) {
      debug(err.message);
      const error: ErrorRPCType = {
        context: {
          action: `${NAMESPACE}:${action}`,
          params: {
            coin,
            action,
            args
          }
        },
        type: NAMESPACE,
        message: err.message,
        ok: 'failed'
      };
      return error;
    }
  };
}
